import React from "react";
import '../App.css';
import { Image, HStack } from '@chakra-ui/react'


function Nav() {

    const handleClick = (anchor) => {
        const id = `${anchor}-section`;
        console.log(id)
        const element = document.getElementById(id);
        if (element) {
          element.scrollIntoView({
            behavior: "smooth",
            block: "start",
          });
        }
      };

    return (
        <div className="navbar">
            <Image src="https://i.ibb.co/5TyTXRr/Logo.jpg" />
            <HStack>
                <a href="#projects" onClick={() => handleClick("about")}>About</a>
            </HStack>
        </div>
    );
}

export default Nav;
